import React, { useState } from 'react'

const News = (props) => {
  const [current, setCurrent] = useState(0)
  let i = 1;

  const Devider = (arr, n = 4) => {
    let data = [];
    let len = parseInt(arr.length / n);
    let iter = 0;
    for (let row = 0; row < len; row++) {
      let temp = []
      for (let col = 0; col < n; col++) {
        temp.push(arr[iter])
        iter++;
      }
      data.push(temp)
    }
    iter--;
    let temp = []
    len = parseInt(arr.length % n);
    for (let col = 0; col < len; col++) {
      temp.push(arr[iter])
      iter++
    }
    data.push(temp)
    return data;
  }


  let books = Devider(props.data, 10);

  const navigator = () => {
    const rows = [];
    for (let i = 0; i < books.length; i++) {
      if (current === i)
        rows.push(<div onClick={() => setCurrent(i)} className="pages p1 lightdiv brd2 row flexAIC flexJCC boxActive"><h3>{i + 1}</h3> </div>);
      else
        rows.push(<div onClick={() => setCurrent(i)} className="pages p1 darkdiv brd2 row flexAIC flexJCC"><h3>{i + 1}</h3> </div>);
    }
    return rows;
  }



  return (
    <div className="navBody flexSB" >
      {

        books[current].map(x => {
          return <div className="row w80 lightdiv brd2 p1 m2 flexAIC newsbox">
            <img src={(x.images[0] == undefined || x.images[0] == "" || x.images[0] == "string") ? "maxresdefault.jpg" : (Array.isArray(x.images) ? x.images[0] : x.images)} className="image" style={{ width: "30%" }} alt="" />
            <div className="col flexSB p3 details" style={{ width: "70%", minHeight: "180px" }}>
              <h1 className='lightdiv'>{x.title}</h1>
              <h5>Category: {x.category.name}</h5>
              <p style={{ color: "grey" }}>{x.description}</p>
              <h3 style={{ color:"black" }}>{x.price}</h3>
              <div className="row flexEnd"><a className="lightdiv linker" href={x.category.name}> MORE...<i class="fa fa-angle-double-right" aria-hidden="true"></i></a></div>

            </div>
          </div>
        })

      }
      <div className="row w80 lightdiv brd2 p1 m2 flexAIC flexSB newsbox wrap">

        <div onClick={() => setCurrent(prev => (prev !== 0) ? prev - 1 : prev)} className="pages p1 lightdiv brd2 row flexAIC flexJCC boxActive"><h3><i className="fa fa-angle-double-left" aria-hidden="true"></i></h3> </div>
        {navigator()}
        <div onClick={() => setCurrent(prev => (prev !== books.length - 1) ? prev + 1 : prev)} className="pages p1 lightdiv brd2 row flexAIC flexJCC boxActive"><h3><i className="fa fa-angle-double-right" aria-hidden="true"></i></h3> </div>




      </div>
    </div>
  )
}

export default News
