    const [state,setState]=useState({lat:26.67775,long:83.3556})
    const submitted=(e)=>{
        e.preventDefault();

    if(e.target.lat.value && e.target.long.value){
     state.lat=e.target.let.value
     state.long=e.target.long.value
     e.target.reset();
    }
    else{
        return alert("Please Enter Valid Details!")
    }

    }
    console.log(state)
    if(!isLoaded)
    return <h2>Is Loading...</h2>

  return (

    <div className="navBody colflexAIC  flexSB" >
