            <input type="text" name="lat" placeholder='Latitude'/>
            <button type='submit'>Locate</button>
        </form>
    <GoogleMap zoom={10} center={{lat:26.67775,lng:83.3556}} mapContainerClassName="map"/>
     </div>
  )
}

export default Map
